INSERT INTO `tbl_sanpham` (`Ma`, `TenSanPham`, `MaHinh`, `SoLuongTon`, `MoTa`, `XuatXu`, `ThuongHieu`, `MaLoai`, `MaDVT`, `GiamGia`, `Search`) VALUES
(1, 'Bộ sản phẩm phục hồi và chống lão hóa Liftactiv Specialist Peptide-C Anti Aging Ampoule', '9898248340580', 98, 'Bộ sản phẩm gồm:\n- 01 x Dưỡng chất Peptide-C cô đặc Liftactiv Ampoule Vichy\n- 02 x Kem chống nắng bảo vệ và giúp giảm các dấu hiệu lão hóa Ideal Soleil Anti-Age SPF50+ 3ml\n- 01 x Kem dưỡng ngày chống lão hóa Vichy Liftactive 15ml\nThông tin chi tiết:\n1. Dưỡng chất Peptide-C cô đặc Liftactiv Ampoule Vichy với hàm lượng Vitamin C nguyên chất phức hợp 5 thành phần chống oxy hóa rất mạnh mẽ, hiệu quả da trẻ trung tươi trẻ sau 10 ngày.\n- Giúp da sáng da mờ thâm sm nám mờ nếp nhăn, chống oxy hóa & lão hóa.\n- Kết cấu dạng tinh chất lỏng thấm rất nhanh.\nHướng dẫn sử dụng:\n- Sử dụng sáng hoặc tối (tốt nhất).\n- Lấy 3 -5 giọt sản phẩm ra tay thoa lên trán, mũi, cằm, 2 bên má.\n- Thoa ngang từ trong ra ngoài & từ trên xuống dưới.\n- Dùng trước bước Kem dưỡng da.\n2. Kem Chống Nắng Bảo Vệ Và Giúp Giảm Các Dấu Hiệu Lão Hóa Ideal Soleil Anti-Age SPF50 với chiết xuất từ trà đen và vitamin C đậm đặc từ quả Camu Camu trong Kem Chống Nắng Vichy Bảo Vệ Da, Chống Lão Hóa Ideal Soleil Anti-Aging có tác dụng chống lão hóa, bảo vệ làn da khỏi tác hại của nắng, thuốc lá và ô nhiễm.\nHướng dẫn sử dụng\n- Sử dụng buổi sáng như kem lót nền trang điểm, sau bước dưỡng da.\n- Lấy 1 lượng bằng 2 hạt bắp chấm lên 5 điểm: Trán, mũi, 2 má & cằm đồng thời thoa vùng cổ\n- Thoa từ trong ra ngoài & từ trên xuống dưới.\n- Nếu hoạt động đi bơi đi biển, hoạt động nhiều thì nên thoa lại sau mỗi 2 tiếng\n3. Kem dưỡng ngày chống lão hóa Vichy Liftactive 15ml với công thức LiftActiv Supreme giúp giảm những dấu hiệu lão hóa dễ nhận thấy như: da xỉn màu, các vết nhăn ở vùng trán, vết chân chim ở gần khóe mắt và những dấu hiệu lão hóa khác. Sau 1 tháng sử dụng, nếp nhăn mờ dần 74% đồng thời giúp da săn chắc hơn, trả lại cho bạn làn da mịn màng, sáng bóng.\nHướng dẫn sử dụng\n- Sử dụng sáng/ tối ngày 2 lần.\n- Lấy 1 lượng bằng hạt bắp chấm đều lên Trán - Mũi - Cằm & 2 bên má. Thoa theo chiều ngang từ trên xuống dưới & từ trong ra ngoài.\n- Có thể dùng làm kem lót trang điểm\nThông tin thương hiệu:\nVichy là thương hiệu nổi tiếng của Pháp được thành lập vào năm 1931 tại thành phố cùng tên Vichy bởi bác sĩ da liễu Haller và nhà kinh doanh Guerin. Với nhiều năm kinh nghiệm, viện nghiên cứu Vichy đã sáng tạo ra nhiều phương thức mới và hiệu quả để chăm sóc sức khỏe làn da. Tất cả sản phẩm Vichy được kiểm nghiệm một cách toàn diện và khắt khe dưới sự giám sát của chuyên gia da liễu về độ an toàn và dịu nhẹ cho da, đặc biệt đối với làn da nhạy cảm. Ngoài ra, các sản phẩm của Vichy đã được chứng minh không gây dị ứng, không gây mụn, chứa nước khoáng Vichy giàu khoáng chất có tác dụng củng cố và tăng cường tái tạo da.\n\nNSX: Xem trên bao bì\n\nXuất xứ thương hiệu: Pháp\n\nNơi sản xuất: Pháp\n\nThành phần: In trên bao bì\n\nHướng dẫn bảo quản: Nơi thoáng mát, tránh ánh nắng trực tiếp\n\nHSD: 3 năm kể từ ngày sản xuất', 'Pháp', 'Vichy', 1, 1, 10, 'bo san pham phuc hoi va chong lao hoa liftactiv specialist peptide-c anti aging ampoule'),
(2, 'Dưỡng Chất Làm Sáng Và Cải Thiện Nếp Nhăn Vichy Lift Activ Vitamin C 15% (10ml)', '1585179650793', 100, 'Dưỡng Chất Làm Sáng Và Cải Thiện Nếp Nhăn Vichy Lift Activ Vitamin C 15% (10ml) với 15% Vitamin C nguyên chất phức hợp 5 thành phần chống oxy hóa rất mạnh mẽ, hiệu quả da trẻ trung tươi trẻ sau 10 ngày.\nThành phần: 0,1% FRAGMENTED HYALURONIC ACID 0,2% NATURAL NEOHESPERIDINE 0,25% MARITIME PINE 0,5% PURE VITAMIN E + VICHY MINERALIZING WATER.\nGiups sáng da mờ thâm sạm nám mờ nếp nhăn, chống oxy hóa & lão hóa.\nKết cấu dạng tinh chất lỏng thấm rất nhanh.\nHướng dẫn sử dụng:\nSử dụng sáng hoặc tối (tốt nhất).\nLấy 3 -5 giọt sản phẩm ra tay thoa lên trán, mũi, cằm, 2 bên má.\nThoa ngang từ trong ra ngoài & từ trên xuống dưới.\nDùng trước bước Kem dưỡng da.', 'Pháp', 'Vichy', 1, 2, 25, 'duong chat lam sang va cai thien nep nhan vichy lift activ vitamin c 15% (10ml)'),
(3, 'Lis Lovin\' Skin - bí quyết dành lại 10-15 năm tuổi xuân tại nhà chỉ trong 3 tháng', '6859892204621', 97, 'Hơn 30 cuộc thử nghiệm trong suốt 5 năm đã giúp nhóm chuyên gia tại Viện nghiên cứu LiS đưa ra kết luận chắc chắn rằng: “Tinh chất thông đỏ Pháp cô đặc cho tác động đáng kinh ngạc trên làn da của phụ nữ Việt Nam mà khó loại thảo dược nào bì được”, nhờ cơ chế song song mạnh mẽ:\n• Tạo màng chắn 3 tầng bảo vệ da trước tác động của tia UV từ ánh nắng mặt trời. Kìm hãm tình trạng tăng sinh sắc tố da quá mức.\n• Chống oxy hóa và chống viêm gấp 20 lần vitamin C và 50 lần vitamin E giúp da tăng cường tái tạo tế bào mới trẻ khỏe, trắng sáng thay thế các tế bào da bị nám sạm, thâm, nhăn.\n• Kích thích gần 1 triệu tế bào sản sinh collagen làm da căng phẳng, xóa mờ và lấp đầy các nếp nhăn. Đồng thời bù nước cho làn da mịn mượt nhờ thúc đẩy Axit hyaluronic.\nTừ đó, tinh chất thông đỏ Pháp cô đặc được nhập khẩu về Việt Nam trong sản phẩm LiS (Lovin’ skin), loại gel an toàn từ thiên nhiên. Chẳng có lời hứa hẹn nào đáng tin cậy hơn sự trải nghiệm của chính bạn, vì thế hãy thử dùng LiS đều đặn 2 lần mỗi ngày để bạn tận mắt thấy tình trạng nám sạm – nếp nhăn được cải thiện rõ rệt.\nCông dụng:\n• Giúp làm mờ vết nhăn, vết thâm, sạm, nám, tàn nhang trên da.\n• Giúp da trắng sáng đều màu\n• Giúp ngăn ngừa lão hoá, nhăn da.\nCách dùng:\nBước 1: Rửa sạch toàn bộ vùng da mặt và da cổ nhằm loại bỏ bụi bẩn, chất nhờn giúp LiS có thể thẩm thấu sâu, nuôi dưỡng da.\nBước 2: Thoa một lớp gel LiS mng lên toàn bộ vùng da mặt và da cổ 2 lần / ngày:\n* Dng LiS vào buổi sáng như lớp kem lót trang điểm\n* Dùng LiS vào buổi tối trước khi đi ngủ\nBước 3: Sau khi thoa LiS, để lớp gel khô thoáng tự nhiên khoảng 2-3 phút trước khi trang điểm hoặc đi ngủ.\nBảo quản: để nơi khô ráo, bảo quản ở nhiệt độ phòng, tránh ánh sáng trực tiếp\nNSX/HSD: xem trên bao bì sản phẩm', 'Việt Nam', 'New Tech Pharm', 1, 2, 10, 'lis lovin\' skin - bi quyet danh lai 10-15 nam tuoi xuan tai nha chi trong 3 thang'),
(4, 'Tinh Chất Chống Nhăn Làm Săn Chắc Và Trẻ Hóa Làn Da Vichy 100812134 (30ml)', '3337871323059', 100, 'Tinh Chất Chống Nhăn Làm Săn Chắc Và Trẻ Hóa Làn Da Vichy 100812134\nThuộc dòng sản phẩm Lift Activ - Rhamnose có tác dụng thúc đẩy quá trình tái tạo da tại lớp Derm Source - nơi sản sinh sợi collagen duy trì độ dàn hồi của da, cho hiệu quả chống lão hóa da toàn diện: làm săn chắc và giảm quá trình hình thành nếp nhăn cũng như các dấu hiệu lão hóa.\nTinh Chất Chống Nhăn Làm Săn Chắc Và Trẻ Hóa Làn Da Vichy 100475919 - 30ml với công thức giàu dưỡng chất nuôi dưỡng và phục hồi cấp kỳ làn da lão hóa, bạn sẽ không phải lo lắng bởi những nếp nhăn trn khuôn mặt hay các vết đốm, lão hóa. Hoàn toàn ự tin với làn da trẻ trung rạng rỡ dù ở độ tuổi nào.\nHiệu Quả\n- Sau 1 phút : Da mềm mại hơn + 32%\n- Sau 10 phút : 10% Giảm thiểu nếp nhăn rõ rệt.\n- Su 1 tháng: Công nghệ chống lão hóa da hiệu quả.\n- Vùng quanh mắt: -27%\n- Vùng trán: -17%\n- Vùng quanh miệng: -16%\n- Cổ: -14%\nThành phần\n- Suối nguồn tươi trẻ của làn da.mang lại hiệu quả săn chắc và chống các dấu hiệu lão hóa da.\n- Phù hợpvới mọi loại da, ngay cả da rất nhạy cảm. Hoạt hất vượt trội: Rhamnose10% hiệu quả trong việc tăg cường cơ cấu da và hoàn thiện làn da lão hóa.\n- Vitamin C: Tăng trưởng nguyên bào sợi.\n- Gia tăng số lượng protein.\n- Hyaluronic Acid 0,4 % len lỏ vào các mô dưới da giúp nâng đỡ & săn chắc da.\nĐộ an toàn\n- Phù hợp với da nhạy cảm.\n- Không Paraben\n- Chứa Vichy Thermal Spa water.\n- Kim nghiệm 100% trên làn da Châu Á nhạy cảm.\nHướng dẫn sử dụng\n- Sử dụng sáng/ tối ngày 2 lần.\n- Dùng hàng ngày buổi sáng/ tối sau bước làm sạch với sữa rửa mặt, cân bằng với nước hoa hồng và làm dịu với nước khoáng dưỡng da. Thoa một lớp mỏng lên da. Mát xa nhẹ nhàng để kem thấm sâu vào trong da.\n- Dung tích: 30ml.\nXuất xứ: Vichy Pháp.', 'Pháp', 'Vichy', 1, 2, 30, 'tinh chat chong nhan lam san chac va tre hoa lan da vichy 100812134 (30ml)'),
(5, 'Kem Dưỡng Da Chống Lão Hóa Rosanna Advanced Age Radiance Moisturizer', '8241941421642', 100, 'Công dụng của kem dưỡng da chống lão hóa Rosanna Advanced Age Radiance Moisturizer\nKem dưỡng Rosanna Advanced Age Radiance Moisturizer là dòng sản phẩm được sử dụng phổ biến trên thị trường do hiệu quả tuyệt vời mà sản phẩm mang lại. Với thành phần từ các tinh chất tự nhiên có tác dụng chống lại các gốc tự do, ngăn ngừa sự hình thành và làm mờ vết nhăn trên khuôn mặt, làm chậm quán trình lão hoá làn da.\nDuy trì và cung cấp độ ẩm tối ưu để cho da luôn mịn màng và tăng cường năng lượng cũng như sức đề kháng cho da giúp phục hồi thương tổn, tái tạo tế bào da mới và nuôi dưỡng da trắng sáng, tự nhiên.\nKem Rosanna của Úc với tinh dầu hoa bất tử có tác dụng chống lại các gốc taự do, ngăn chặn sự hình thành nếp nhăn trên khuôn mặt.\nTinh chất mờ cừu tinh khiết, nhau thai cừu và tinh dầu gan cá mập bổ sung dưỡng chất cho làn da luôn tươi trẻ với Kem Làm Trắng Da của Úc Rosanna Advanced Age Radiance Moisturiz.\nThành phần 100% tinh chất tự nhiên nên ko gây kích ứng da và độc hại, hương thơn nhẹ nhàng và dễ chịu.\nCollagen, Q10 chống lão hoá, giữ độ đàn hồi cho da.\nSản phẩm Rosanna Advanced Age Radiance Moisturizer phù hợp với mọi làn da ngay cả những làn da nhạy cảm nhất.\nRosanna Advanced Age Radiance Moisturizer giúp bạn có được làn da trắng hồng theo thời gian.', 'Úc', 'Rosanna', 1, 2, 16, 'kem duong da chong lao hoa rosanna advanced age radiance moisturizer'),
(6, 'Kem Dưỡng Ban Đêm Giúp Ngăn Ngừa Lão Hóa Eucerin Hyaluaron- Filler Elasticity (50ml)', '5729187487927', 100, 'Kem Dưỡng Ban Đêm Giúp Ngăn Ngừa Lão Hóa Eucerin Hyaluaron- Filler Elasticity (50ml) chứa hoạt chất Arctiin được biết đến là chất hỗ trợ quá trình tái tạo collagen trong da và Silymarin là chất chống Oxy hóa hiệu quả\nBảo vệ collagen và sợi Elastic khỏi sự mất cân bằng lượng oxy hóa đồng thời với thành phần Argan Oil sẽ giúp nuôi dưỡng chuyên sâu làn da vào ban đêm mang đến cho bạn cảm nhận về làn da săn chắc, mịn màng và tràn đầy sức sống khi sử dụng thường xuyên.\nVói công thức độc đáo, gồm:\nSylimarin: Chiết xuất tự nhiên từ cây kế sữa, giúp bảo vệ collagen và elastin khỏi quá trình lão hóa, tái tạo cấu trúc da.\nArctiin: Chiết xuất tự nhiên từ cây ngưu bàng, thúc đẩy quá trình tái tạo collagen trong tế bào da, giúp da căng mịn.\nHyaluronic Acid: Phân tử cao và thấp, dễ dàng dung nạp vào da, làm đầy nếp nhăn sâu rõ rệt.\nArgan Oil: Chiết xuất tự nhiên từ hạt nhân của Argan, giúp nuôi dưỡng da mềm mịn và thúc đẩy quá trình tái tạo da ban đêm hiệu quả hơn.', 'Ba Lan', 'Eucerin', 1, 2, 22, 'kem duong ban dem giup ngan ngua lao hoa eucerin hyaluaron- filler elasticity (50ml)'),
(7, 'Uriage Depiderm Soin Cible Anti-Taches: Kem Chăm Sóc Da Nám, Tàn Nhang (15 ml)', '7784034133622', 100, 'Uriage Depiderm Anti-Brown Spot Targeted Care (15 ml)\nKem Giảm Nám, Tàn Nhang Uriage Depiderm Anti-Brown Spot Targeted Care giảm kích thước và màu sắc từng đốm nám, tàn nhang. Kem dễ thẩm thấu, hiệu quả cao.\nCách dùng Uriage Depiderm Anti-Brown Spot Targeted Care\nThoa Uriage Depiderm Anti-Brown Spot Targeted Care mỗi buổi sáng và tối, trực tiếp lên đốm tàn nhang và nám trên mặt, vai và bàn tay. Tránh tiếp xúc gần mắt.\nThành phần hoạt chất\nCx complex (Pea and sucrose extracts, Vitamin C, , Enoxolone)\nAHA\nĐặc điểm\nKhông gây dị ứng\nKhông hương liệu\nDùng cho: Da mặt, da tay, ngực.\nDung tích: Tuýp 15 ml\nXuất xứ: Uriage – Pháp', NULL, 'Uriage', 1, 2, 0, 'uriage depiderm soin cible anti-taches: kem cham soc da nam, tan nhang (15 ml)'),
(8, 'Kem Dưỡng Da Ban Đêm Chống Lão Hóa Làm Mờ Nếp Nhăn Và Tái Tạo Da EUBOS Anti-age Hyaluron Repair Filler Night (50ml)', '1806017875629', 100, 'Các nghiên cứu khoa học gần đây đã phát hiện, thúc đẩy khả năng giao tiếp của các tế bào sẽ giúp ngăn ngừa quá trình lão hóa. Khi da bị lão hóa, ngoài việc khả năng giao tiếp của tế bào bị giảm, khả năng đàn hồi và cấu trúc (elastin, collagen), hàm lượng Hyaluron và hàm lượng lipid cũng giảm theo khiến da chùng xuống và hình thành các nếp nhăn.\nBan đêm, trong khi ngủ, cơ thể sẽ tự phục hồi- đặc biệt là da: các tế bào tương tác với nhau trong một quá trình trao đổi đặc biệt, giao tiếp tế bào được thực hiện mạnh mẽ giúp loại bỏ các tác nhân gây căng thẳng, lão hóa, phục hồi các thương tổn.\nDo đó, EUBOS đã nghiên cứu và phát triển Kem Dưỡng Da Ban Đêm Chống Lão Hóa EUBOS Anti-age Hyaluron Repair Filler Night với thế hệ hoạt chất mới nhất nâng cao khả năng phục hồi tự nhiên của da. Công thức đặc biệt kem dưỡng chống lão hóa ban đêm EUBOS sử dụng các phát hiện khoa học mới nhất tăng cường liên lạc giữa các tế bào da kết hợp cùng chiết xuất thảo dược đem lại tác dụng vượt trội.\nKem Dưỡng Da Ban Đêm Chống Lão Hóa EUBOS Anti-age Hyaluron Repair Filler Night hỗ trợ chăm sóc da chuyên sâu, ngăn ngừa lão hóa, hỗ trợ quá trình phục hồi và tái tạo da tự nhiên trong luc ngủ.\nKem Dưỡng Da Ban Đêm Chống Lão Hóa EUBOS Anti-age Hyaluron Repair Filler Night với công thức đặc biệt kết hợp Dermcom, lựu trắng, chiết xuất tảo và hoa mộc lan thúc đẩy tổng hợp collagen và elastin, làm giảm nếp nhăn, tăng khả năng phục hồi và chống viêm.\nDermCom là hoạt chất đầu tiên trên thế giới có khả năng thúc đẩy sản sinh ra nhiều collagen và elastin (collagen tăng lên 115%, elastin tăng lên 25%).\nChiết xuất từ củ hoa nghệ tây thúc đẩy giao tiếp tế bào và kích thích hiệu quả tổng hợp collagen và elastin.\nChiết xuất từ cây dâm bụt trắng và baobab thúc đẩy liên kết giữa các tế bào, giúp giảm nếp nhăn trên khuôn mặtmang lại \"hiệu ứng Botox tự nhiên\" và chống lại sự thiếu hụt lipid nội bào.\nChiết xuất tảo (Alaria esculenta) bảo vệ collagen và elastin khỏi sự thoái hóa. Tăng cường liên kết giữa các tế bào biểu bì, thúc đẩy giao tiếp tế bào và hô hấp tế bào. Ngoài ra, chiết xuất tảo còn kích thích sự hình thành collagens và proteoglycan (hyaluron) và có tác dụng hồi sinh và làm săn chắc da.\nChiết xuất mộc lan chống viêm, chống oxy hóa và bảo vệ da chống lại tổn thương.\nDẫn xuất isosorbide đặc biệt giúp thúc đẩy tổng hợp Aqua porin-3 và hyaluronic - kéo dài hiệu quả trên da.\nChiết xuất dầu bơ, dầu hạt mỡ, dầu argan cho làn da mịn màng, mềm mại.', 'Đức', 'EUBOS', 1, 2, 0, 'kem duong da ban dem chong lao hoa lam mo nep nhan va tai tao da eubos anti-age hyaluron repair filler night (50ml)'),
(9, 'Kem dưỡng ban đêm Eucerin Q10 ACTIVE (50ml)', '4870533280813', 100, 'Kem dưỡng ban đêm Eucerin Q10 ACTIVE (50ml) với công thức ngăn ngừa vết nhăn chuyên biệt cho làn da nhạy cảm và có dấu hiệu lão hóa sớm.\nSản phẩm chứa công thức Coenzyme Q10 độc quyền từ Eucerin giúp tăng khả năng bảo vệ tự nhiên của tế bào, từ đó chống lại các gốc tự do giúp cải thiện bề mặt da sau mỗi đêm*.\nSử dụng thường xuyên để da săn chắc và mịn màng hơn. Sản phẩm không chứa paraben và chất tạo màu nhân tạo gây dị ứng trên da.', 'Đức', 'Eucerin', 1, 2, 0, 'kem duong ban dem eucerin q10 active (50ml)'),
(10, 'Kem ngăn ngừa lão hóa ban ngày Eucerin Q10 Active Day (50ml)', '7767776511262', 100, 'Kem ngăn ngừa lão hóa ban ngày Eucerin Q10 Active Day (50ml) chuyên biệt giúp làm trẻ hóa tế bào da và đồng thời ngăn ngừa các dấu hiệu lão hóa xuất hiện trên làn da nhạy cảm.\nĐược bổ sung thêm chất chống oxy hóa, vitamin C, E và H (Biotin), sản phẩm có khả năng điều trị tình trạng viêm và nuôi dưỡng làn da thêm ẩm mịn. Từ đó, Q10 ACTIVE day giúp bảo vệ da hiệu quả khỏi các gốc tự do – tác nhân chính gây lão hóa.\nSản phẩm không chứa hoạt chất gây mẫn cảm trên da khô và nhạy cảm như mùi hương, chất tạo màu, cồn hóa học và paraben.', 'Đức', 'Eucerin', 1, 2, 45, 'kem ngan ngua lao hoa ban ngay eucerin q10 active day (50ml)'),
(11, 'Tinh Chất Vàng Dưỡng Trắng, Tái Tạo Da 3W Clinic Collagen & Luxury Gold 150ml', '1615638390783', 100, 'Tinh Chất Collagen & Luxury Gold 3W Clinic là sản phẩm thuộc dòng dưỡng da cao cấp với Tinh Chất Vàng, chiết xuất Collagen, được chiết xuất theo công nghệ nano vô trùng hiện đại, có tác dụng tăng sản sinh Collagen, cải thiện độ đàn hồi cho da, ngăn ngừa lão hóa da, đem đến làn da căng tràn sức sống.\n- Các dưỡng chất giúp cung cấp độ ẩm cần thiết giúp cho da không bị khô, se khít lỗ chân lông cho da, giúp da luôn mượt mà.\n- Chiết xuất rau má giúp làm dịu, làm sạch da, ngăn ngừa viêm nhiễm và đẩy nhanh quá trình liền sẹo cho da.\n- Sản phẩm giúp loại bỏ tế bào chết, thanh lọc độc tố cho da, kích thích các tế bào tái tạo ra lớp da mới trắng hồng, làm mờ các vết thâm nám, tàn nhang, xỉn màu, đem lại cho bạn làn da trắng hồng rạng rỡ.\n\n- Dạng gel trong suốt, mát lạnh và thẩm thấu ngay khi bạn thoa vào mặt (cổ) mà không gây cảm giác nhờn rít, khó chịu.\n\n\n\n\n\n\n\nHƯỚNG DẪN SỬ DỤNG\n\n- Rửa sạch mặt, có thể dùng nước hoa hồng sau bước rửa mặt để giúp da hấp thụ dưỡng chất tốt hơn.\n- Lấy một lượng tinh chất vừa đủ thoa đều lên mặt, có thể bôi dưỡng vùng cổ.\n- Dùng tay massage và vỗ thật nhẹ nhàng để tinh chất thẩm thấu sâu vào da\nLưu ý:\n- Bảo quản nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp và nhiệt độ cao.\n- Bao bì sản phẩm có thể thay đổi theo từng lô hàng từ nhà sản xuất nhé ạ!', 'Hàn Quốc', '3W Clinic', 1, 2, 0, 'tinh chát vàng duõng tráng, tái tạo da 3w clinic collagen & luxury gold 150ml'),
(12, 'Kem Dưỡng Da Chống Nắng Ban Ngày Chống Lão Hóa SPF 20+ UVA EUBOS Anti-age Hyaluron Repair Filler Day (50ml)', '7026491504226', 100, 'Kem Dưỡng Chống Lão Hóa Ban Ngày EUBOS Anti-age Hyaluron Repair Filler Day giúp làm mờ nếp nhăn giúp tái tạo da, phục hồi sự tươi trẻ từ bên trong.\nCông thức đặc biệt kết hợp 3 chuỗi Hyaluronic axit với độ dài khác nhau hình thành lớp bảo vệ giúp khóa ẩm trên bề mặt da,thẩm thấu sâu vào bên trong tế bào, trở thành một phần trong quá trình trao đổi chất hỗ trợ tái tạo tế bào.\nKem Dưỡng Da Chống Nắng Ban Ngày Chống Lão Hóa SPF 20+ UVA EUBOS bổ sung HA (Hyaluronic Acid) giúp làn da được dưỡng ẩm nhiều hơn, các nếp nhăn được làm đầy, cảm nhận sự tươi trẻ của làn da sau 4 tuần sử dụng.\nMatrikines, phức hợp peptide đóng vai trò như thành phần giao tiếp tế bào, có khả năng kích kích tái tạo gấp đôi collagen và elastin. Cấu trúc da được phục hồi và tăng cường. Các nếp nhăn và sẹo được tái tạo từ bên trong và làn da trở nên mịn màng hơn.\nKem Dưỡng Da Chống Nắng Ban Ngày Chống Lão Hóa SPF 20+ UVA EUBOS bổ sung Coenzyme Q10 là một coenzyme quan trọng để sản xuất năng lượng trong tế bào và là chất chống oxy hóa hoặc oxy hóa gốc tự do rất mạnh mẽ. Coenzyme Q10 xuyên qua các lớp tế bào của da giúp cải thiện các nếp nhăn hình thành do quá trình lão hóa gây nên. Coenzyme Q10 đã được chứng minh bảo vệ các tế bào Kerotinocyte cấu tạo nên tế bào da người khỏi tác hại oxy hóa gây ra bởi tia cực tím.\nVitamin B3 bảo vệ cấu trúc da, tăng cường hệ thống miễn dịch tự nhiên của da, tăng tốc độ tái tạo da và ngăn ngừa sự hình thành các đốm tăng sắc tố.\nDầu từ bơ hạt mỡ và hạt mắc ca hỗ trợ cân bằng lượng lipid và độ ẩm của da, tăng cường khả năng bảo vệ tự nhiên của da.', 'Đức', 'EUBOS', 1, 2, 0, 'kem duong da chong nang ban ngay chong lao hoa spf 20+ uva eubos anti-age hyaluron repair filler day (50ml)'),
(13, 'ONE TODAY - KEM NGỪA NÁM', '9675357562870', 100, 'Trọng lượng: 08g\nCông Dụng:\nKem nám One Today với tinh chất ốc sên, bột ngọc trai cùng sữa dê giúp nuôi dưỡng và giữ ẩm cho làn da. Vitamin C và hoạt chất dưỡng trắng da kết hợp cùng công thức cải tiến giúp thấm sâu vào da hiệu quả, làm giảm sự hình thành sắc tố melanin từ đó ngăn ngừa và làm mờ các vết nám hiệu quả, nuôi dưỡng làn da trắng mịn tràn đầy sức sống.', 'Việt Nam', 'One Today', 1, 2, 0, 'one today - kem ngua nam'),
(14, 'Serum Squalene Chống Lão Hóa Và Dưỡng Ẩm Botani Olive Skin Serum BPSO003 (15ml)', '8625699104161', 100, 'Serum Squalene Chống Lão Hóa Và Dưỡng Ẩm Botani Olive Skin Serum BPSO003 (15ml)\nSerum Squalene Chống Lão Hóa Và Dưỡng Ẩm Botani Olive Skin Serum BPSO003 (15ml) là một trong 5 loại serum mang lại hiệu quả chống lão hóa tốt nhất do người tiêu dùng bình chọn trên tạp chí BeautyHeaven tháng 12 năm 2013. Với các thành phần dưỡng chất có trong serum, làn da của bạn sẽ trở nên mịn màng, xóa mờ các nếp nhăn và rạng rỡ ngay lập tức mà không cần lo lắng về những tác hại khác.\nThông tin sản phẩm\nLoại sản phẩm\nOlive Skin Serum là loại serum chống lão hóa giúp làn da của bạn trở nên mịn màng và đầy sức sống.\nThành phần và công dụng\nTrong serum có chứa 99.7% Olive Squalene có khả năng kích thích Collagen và Elastin, tạo đàn hồi cho da, giảm tì vết, thúc đẩy quá trình tái tạo tế bào mới, phục hồi sự tươi trẻ cho làn da.\n0.3 % Vitamin C (thành phần bảo quản tự nhiên và chất chống oxy hóa hiệu quả), và Vitamin E (chất chống oxy hóa tự nhiên) và Lecithin (có sẵn tự nhiên trong da) giúp thúc đẩy các hoạt chất khác thâm nhập sâu hơn vào da.\nChỉ số chống nắng SPF15 giúp bảo vệ da tránh khỏi các tác hại của ánh nắng mặt trời.\nLoại da phù hợp\nSản phẩm chứa các chiết xuất hoàn toàn từ thiên nhiên nên phù hợp với mọi loại da.\nHuớng dẫn sử dụng\n- Rửa mặt thật sạch bằng sữa rửa mặt (Purify facial cleanser hoặc Olive soothing cleanser) và dùng khăn sạch thấm khô.\n- Sau đó sử dụng Olive skin serum: nhỏ 2-3 giọt lên ngón tay, xoa nhẹ nhàng hai ngón tay để làm ấm serum rồi chấm nhẹ nhàng lên mặt và vùng mắt hoặc vùng da bị tổn thương.\n- Sử dụng làm lớp phủ bảo vệ trước khi trang điểm.\n- Sử dụng ngày 2 lần, dùng được cả ngày và đêm vì khả năng thẩm thấu nhanh và sâu nên dưỡng chất nuôi dưỡng làn da sẽ được hấp thụ vào da ngay lập tức cho bạn cảm nhận được làn da mềm mại mịn màng như lụa mà không gây nhờn bóng.\n- Sản phẩm này có thể dùng để chữa nứt núm vú, sử dụng ngày 2 lần sau khi cho con bú.', 'Úc', 'Botani', 1, 2, 0, 'serum squalene chong lao hoa va duong am botani olive skin serum bpso003 (15ml)'),
(15, 'KEM DƯỠNG TRẮNG - NGỪA LÃO HÓA DA ONE TODAY', '7299662024773', 100, 'Công Dụng:\nVới chiết xuất từ bột ngọc trai, sữa dê và tinh chất ốc sên giúp cung cấp dưỡng chất cho làn da trắng mịn hiệu quả. Công thức cải tiến giúp các vitamin E, C, B3 và hoạt chất dưỡng trắng dễ dàng thấm sâu vào da, ngăn ngừa các dấu hiệu lão hóa da duy trì vẻ đẹp tươi trẻ cho làn da bạn\nThành Phần:\nVaseline, Paraffinum liquidum, Titanium dioxide, Kojic dipalmitate, Talc, Glyceryl stearate, Isopropyl palmitate, Parfum, Phenoxyethanol, Chlorphenesin, Tocopheryl acetate, Niacinamide, Alpha-arbutin, Ascorbyl palmitate, Ricinus communis seed oil, Pearl extract, Snail Secretion filtrate, Goat milk extract, Ubiquinone, Soybean oil, CI 19140.', 'Việt Nam', 'One Today', 1, 2, 0, 'kem duong trang - ngua lao hoa da one today'),
(16, 'Uriage Depiderm SPF50 Soin De Jour Anti-Taches: Kem Trắng Da, Chống Nắng Ban Ngày 30ml', '3826944645155', 100, 'Uriage Depiderm Anti-Brown Spots Daytime Care (30 ml)\nKem Dưỡng Ban Ngày Uriage Depiderm Anti-Brown Spots Daytime Care Ngăn Ngừa Đốm Nâu, Tàn Nhàng. Bảo vệ da và giảm nguy cơ xuất hiện đốm nâu do tia cực tím - yếu tố chính làm tăng sắc tố trên da.\nNgăn tình trạng tăng sắc tố da và ngăn vết đồi mồi xuất hiện. Bảo vệ làn da có sự can thiệp từ bên ngoài như laser, mặt nạ hóa chất,...và ngăn đốm đồi mồi phát triển.\nĐặc biệt giàu nguyên tố vi lượng và muối khoáng, nước khoáng Uriage Thermal Water ở dạng đẳng trương tự nhiên có đặc tính xoa dịu, bù nước và chống gốc tự do.\nThành phần hoạt chất\nNước khoáng Uriage Thermal Water\nHợp chất lọc tia UVA UVB\nCam thảo (Licorice)\nNicotinamide\nVitamin C\nThành phần: Aqua (Water), Dicaprylyl Carbonate, Uriage Thermal Spring Water, Methylene Bis, Benzotriazolyl Tetramethylbutylphenol, Butyl Methoxydibenzoylmethane, Ethylhexil Triazone, Niacinamide, Nylon I2, Triacontanyl PVP, Decyl Glucoside, Ascorbyl Tetraisopalmitate, Butylene Glycol, C20/22 Alkyl Phosphate, C20/22 Alcohols, Phenoxyethanol, Xanthan Gum, Benzoic Acid, Tetrasodium Edta, Tocopheryl Acetate, O Cymen 5 Ol, Propylene Glycol, Glycyrrhiza Glabra (Licorice) Root Extract, Citric Acid, Sodium Hydroxide.\nĐặc điểm\nKhông gây dị ứng\nKhông bít tắc lỗ chân lông\nChỉ định: Dùng để khắc phục khuyết điểm cho da mặt, tay và vai.\nHướng dẫn sử dụng: Thoa Uriage Depiderm Anti-Brown Spots Daytime Care mỗi buổi sáng lên vùng mặt, cổ và bàn tay hoặc lên các đốm nâu. Thoa lại mỗi 2 tiếng trong trường hợp tiếp xúc với ánh nắng. Tránh tiếp xúc với mắt.\nLưu ý:\nGiảm liều lượng xuống 1/2 có thể làm giảm khả năng bảo vệ da khỏi ánh nắng xuống 2-3 lần.\nNên thoa thường xuyên và sau khi hoạt động thể chất.\nNên hạn chế tiếp xúc với ánh nắng ngay cả khi đã thoa sản phẩm.\nTránh để sản phẩm dính vào mắt.\nDung tích: Tuýp 30 ml\nXuất xứ: Uriage - Pháp', NULL, 'Uriage', 1, 2, 0, 'uriage depiderm spf50 soin de jour anti-taches: kem trang da, chong nang ban ngay 30ml'),
(17, 'Kem Dưỡng Ngăn Ngừa Lão Hóa Ban Đêm Eucerin Anti-Age Hyaluron Filler Night Cream (50ml)', '2278396906871', 100, 'Kem Dưỡng Ngăn Ngừa Lão Hóa Ban Đêm Eucerin Anti-Age Hyaluron Filler Night Cream (50ml) với thành phần Hyaluronic Acid giúp làm giảm và ngăn ngừa nếp nếp nhăn, Glycine-saponin tăng cường quá trình sản xuất Hyaluronic Acid tự nhiên trong da.\nDexpanthenol cô đặc hỗ trợ quá trình tái tạo da vào ban đêm.\nNhờ đó, da được chăm sóc kĩ lưỡng và cho cảm giác mềm mại mịn màng hơn.', 'Ba Lan', 'Eucerin', 1, 2, 0, 'kem duong ngan ngua lao hoa ban dem eucerin anti-age hyaluron filler night cream (50ml)'),
(18, 'Kem dưỡng ẩm dành cho da lão hóa Dermaceutic Pháp - Regen Ceutic', '8084025539686', 100, 'Regen Ceutic là sản phẩm mới nhất trong dòng sản phẩm phục hồi da của Dermaceutic. Regen Ceutic chứa Peptides Comples được biết đến với tên thương mại Matrixyl 3000, được chứng minh cực kỳ hiệu quả trong việc kích thích sản sinh Elastin, Acid Hyaluronic và Collagen giúp làm giảm nếp nhăn và các rãnh nhăn trên da.\nCông dụng:\nGiúp tăng tốc độ phục hồi lớp biểu bì da và nuôi dưỡng các tổ chức dưới da, Regen Ceutic được khuyến khích dùng cho da sau điều trị tổn thương như Laser, Peel, lăn kim,…, da nhạy cảm, da khô và da bị lão hóa. Với công thức hoạt chất mạnh mẽ giúp tăng khả năng phục hồi, cung cấp độ ẩm tối ưu cho làn da săn chắc vượt trội\nThành phần hoạt chất:\n3% Peptide Complex (MATRIXYL 3000): Sức mạnh tổng hợp của hai sợi Peptide mang lại hiệu quả tối ưu trong việc kích thích sản xuất Collagen, Hyaluronic Acid và Eslatin giúp chồng lão hóa da từ bên trong.\n3% gel Hyaluronic Acid: Giúp dưỡng ẩm và làm săn chắc da.\nVitamin C và vitamin E: Sự kết hợp giữa hai loại Vitamin này có tác dụng kháng viêm, đồng thời giúp da chống lại các tác hại do ánh nắng mặt trời.\nBơ hạt mỡ: Bơ hạt mỡ là một sản phẩm tự nhiên vô cùng giàu vitamin A, E và F, giúp bảo vệ da khỏi các tia UV cùng các axit béo thiết yếu, ngoài ra còn cung cấp các chất dinh dưỡng hỗ trợ việc sản xuất collagen. Nó cũng là sản phẩm có thể cung cấp cho da một lượng chất chống oxy hóa khổng lồ. Bơ hạt mỡ giúp dưỡng ẩm, trẻ hóa làn da, phục hồi da, giảm viêm, ngăn ngừa và phục hồi các vết nứt da.\nCách sử dụng:\nThoa sản phẩm lên da sau các liệu trình lột da, laser để cảm giác dịu nhẹ và thư thái.\nThoa đều sản phẩm lên mặt vào buổi sáng và tối hoặc bất cứ lúc nào để dưỡng ẩm và chăm sóc da.\nKết quả khoa học:\nKết quả nghiên cứu cho rằng Regen Ceutic có khả năng:\n· Tăng khả năng giữ nước lên 109%\n· Tăng độ săn chắc da lên 32%\n· Giảm 43% số lượng các nếp nhăn.\nVà thực tế:\n· 100% bệnh nhân cho biết da của họ giữ nước tốt, da săn chắc và tươi trẻ hơn.\n· 95% bệnh nhân cho rằng da của họ giảm được tình trạng chùng nhão và tái tạo da mới\n· 100% người sử dụng thử Regen Ceutic đều ra quyết định mua sản phẩm này\nQuy cách đóng gói: 1 chai/40ml\n', 'Pháp', 'Dermaceutic', 1, 2, 0, 'kem duong am danh cho da lao hoa dermaceutic phap - regen ceutic'),
(19, 'KEM DƯỠNG TRẮNG GIÚP TÁI TẠO DA', '4720582514776', 100, 'Trọng lượng: 10g\nCông Dụng:\nVới chiết xuất từ tinh chất ốc sên, bột ngọc trai cùng vitamin E, C và B3 giúp nuôi dưỡng làn da từ bên trong đồng thời giữ ẩm cho da hiệu quả. Đặc biệt công nghệ mới kết hợp tinh chất tế bào gốc và hoạt chất dưỡng trắng hỗ trợ sản sinh tế bào mới tái tạo da, se khít lỗ chân lông và làm trắng da từ bên trong, trả lại làn da mịn màng tươi trẻ.\nThành Phần:\nVaseline, Paraffinum liquidum, Titanium dioxide, Kojic dipalmitate, Talc, Glyceryl stearate, Isopropyl palmitate, Parfum, Phenoxyethanol, Chlorphenesin, Tocopheryl acetate, Malus domestica fruit cell culture extract, Niacinamide, Alpha-arbutin, Ascorbyl palmitate, Ricinus communis seed oil, Pearl extract, Snail Secretion filtrate, Goat milk extract, Lecithin, Soybean oil, CI 19140.', 'Việt Nam', 'One Today', 1, 2, 0, 'kem duong trang giup tai tao da'),
(20, 'Serum Dưỡng Trắng và Trẻ Hóa Da( Whitening Serum )', '2330950241337', 100, 'Mô tả: Sản phẩm được chiết xuất từ 100% thảo dược thiên nhiên Hàn Quốc, được sản xuất trên dây chuyền 5 sao, được BỘ Y TẾ cấp phép lưu hành.\nCông dụng:\n- Nuôi dưỡng làn da trắng sáng, căng bóng, mềm mại\n- Duy trì độ ẩm thiết yếu cho da, chống hình thành nếp nhăn và đẩy lùi các dấu lão hóa da\n- Làm dịu mát da, phục hồi tổn thương, dị ứng do dùng mỹ phẩm hay hóa chất gây ra\n- Cải thiện đàn hồi cho da, ngăn ngừa tình trạng chảy xệ và chùng nhão da\n- Giảm thâm sạm và đánh bật các dấu hiệu nám tiềm ẩn từ bên trong\n- Se khít lỗ chân lông, ngừa mụn và thâm\n- Tăng sức đề kháng cho da, bảo vệ làn da khỏi tác động xấu từ môi trường bên ngoài\nHướng dẫn sử dụng:\nLàm sạch da bằng Sữa rửa mặt Cleansing milk Korena để loại bỏ hết bụi bẩn, vi khuẩn bám trên da.\nDùng từ 2-3 giọt Serum Collagen, thoa đều khắp mặt, kết hợp massage nhẹ nhàng theo chiều xoắn ốc, giúp chất dinh dưỡng thấm sâu vào da, hỗ trợ lưu thông khí huyết, thư giãn cơ mặt.\nNên sử dụng ngày 2 lần vào buổi sáng và tối, sau bước rửa mặt và trước bước dùng kem dưỡng da.\nChỉ tiêu kích ứng: không có\nBảo quản: Nơi khô ráo, thoáng mát, tránh ánh nắng mặt trời. Tránh xa tầm tay trẻ em', 'Việt Nam', 'Korena', 1, 2, 0, 'serum duong trang và tre hoa da( whitening serum )'),
(21, 'Serum Trị Nám Halo Perfect 30ml', '1708712182180', 100, 'Thương hiệu: Halo\n– Dung tích: 30ml\n– Loại da: Da sần, nám, Da nhờn.\nVới các thành phần từ thiên nhiên an toàn lành tính như: Chiết xuất rễ cam thảo làm sáng da tự nhiên tuyệt vời, mà nó còn giúp làm giảm quầng thâm và các vùng da tối màu do sẹo để lại. Mật ong lên men giúp làm đẹp da, giúp da mịn tự nhiên,dầu cám gạo có tác dụng chống lão hóa, ngăn chặn được sự xâm nhập của tia cực tím, cản trở hoạt động bài tiết sắc tố melamin trong biểu bì da. Ngoài ra còn có vitamin e và các khoáng chất cần thiết giữ ẩm nuôi dưỡng làn da khỏe mạnh hơn.\n\nI/ CÔNG DỤNG:\n– Đẹp da, giúp da mịn tự nhiên.\n– Chống lão hoá.\n– Ngăn chặn sự xâm nhập của tia cực tím, cản trở hoạt động bài tiết sắc tố melamin.\n– Giữ ẩm, nuôi dưỡng làn da khoẻ mạnh.\n\nII/ HƯỚNG DẪN SỬ DỤNG:\n– Sau khi rửa mặt xong->toner->Cho 1 lượng serum vừa đủ thoa lên vùng da có sắc tố melamin (nám, tàn nhang).\n– Dùng 2lần/Ngày, thoa kem chống nắng hàng ngày,che chắn khi tiếp xúc ánh nắng mặt trời.\nIII/ THÀNH PHẦN:\n1. WATER\n2. POTASSIUM GLYCYRRHIZINATE\n3. XANTHAN GUM\n4. SODIUM ACRYLATE COPOLYMER\n5. NIACINAMIDE\n6. 3-O-ETHYL ASCORBIC ACID\n7. TOCOPHEROL VITAMIN E\n8. ORYZA SATIVA BRAN OIL\n9. CITRIC ACID\n10. GLUCONOBACTER/HONEY FERMENT FILTRATE\n11. GLYCYRRHIZA GLABRA ROOT EXTRACT\n12. PHENOXYETHANOL\n13. ETHYLHEXYLGLYCERIN\n14. FRAGRANCE\nHiệu quả sau một tuần sử dụng theo hướng dẫn', 'Việt Nam', 'Mỹ Phẩm Halo Việt Nam', 1, 2, 0, 'serum tri nam halo perfect 30ml'),
(22, 'SERUM - TINH CHẤT 10 LOÀI HOA VISKIN - pH 5.4', '6828724409152', 100, 'Serum chiết xuất từ 10 loài hoa theo tiêu chuẩn công nghệ Hàn Quốc. Phù hợp cho mọi làn da, nuôi dưỡng da trắng hồng, da luôn căng mướt, mềm mịn, tăng sinh collagen chống lão hóa, giúp phục hồi nhanh chóng làn da bị tổn thương do dùng kem trộn\n- Serum 10 loài hoa được chiết xuất từ 10 loài hoa, 5 loại thảo dược qúy nhập khẩu từ các nước tiên tiến trên thế giới, hoàn hảo cho việc dưỡng da. Sản phẩm không chứa corticoid, không paraben, không chứa kim loại nặng như: chì , thủy ngân, asen.\n\n- Nhà máy sản xuất sản phẩm được chuyển giao công nghệ toàn phần từ công ty Hanacos Korea đạt tiêu chuẩn cGMP , ISO 9001 : 2008, ISO 9001 : 2009\n\nHướng dẫn sử dụng:\n\nSau khi rửa mặt với sửa rửa mặt và lau khô, cho 1-2 giọt serum vào lòng bàn tay.\nThoa nhẹ nhàng, mát-xa đều lên khuôn mặt khoảng 10 giây.\nVỗ nhẹ lên mặt đến khi khô thoáng.\n\n', 'Việt Nam', 'ViSkin', 1, 2, 0, 'serum - tinh chat 10 loai hoa viskin - ph 5.4'),
(23, 'Kem Dưỡng Trắng Da Tinh Chất Collagen 3W Clinic Collagen Whitening Cream 60ml', '9096520626883', 100, 'Kem dưỡng trắng da 3W Clinic Collagen Whitening Cream là loại kem giàu dinh dưỡng, chứa thành phần làm trắng an toàn Niacinamide giúp giữ cho làn da luôn trắng sáng, làm mờ các vết thâm, nám, tàn nhang, sạm đen và đốm nâu trên da.\n- Đồng thời, sản phẩm với chiết xuất Collagen từ tảo biển và cam thảo có tác dụng tăng tính đàn hồi cho da, kích thích các tế bào tái tạo ra lớp da mới trắng hồng, đem lại cho bạn làn da trắng mịn, căng tràn sức sống.\n- Các dưỡng chất của kem dưỡng trắng da có tác dụng cung cấp độ ẩm cần thiết giúp cho da không bị khô và cũng không quá ẩm.\nHƯỚNG DẪN SỬ DỤNG\n- Rửa sạch mặt, có thể dùng nước hoa hồng sau bước rửa mặt để giúp da hấp thụ dưỡng chất của kem tốt hơn.\n- Lấy một lượng kem dưỡng vừa đủ thoa đều lên mặt, có thể bôi dưỡng vùng cổ.\n- Dùng tay vỗ thật nhẹ nhàng lên da để da hấp thụ dưỡng chất một cách tốt nhất.\nLưu ý:\n- Sử dụng một lượng vừa đủ cho da, tránh bôi quá nhiều khiến da không hấp thụ hết dưỡng chất và gây bí da.\n- Bảo quản nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp và nhiệt độ cao', 'Hàn Quốc', '3W Clinic', 1, 2, 0, 'kem duõng tráng da tinh chát collagen 3w clinic collagen whitening cream 60ml'),
(24, 'Bộ kit dưỡng ẩm làm mờ vết nhăn Murad (Invisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ 10ml +AHA/BHA Exfoliating Cleanser 30ml +Hydro-Glow Aqua Peel + Retinol Youth Renewal Serum 5ml)', '8010034260570', 100, 'Retinol Tri - Active có trong Retinol Youth Renewal Serum là một trong những công nghệ mang tính đột phá trong giới Khoa học - Làm đẹp. Công thức này giúp phát huy tối đa công dụng Retinol trong việc chăm sóc da, đồng thời loại bỏ được các khuyết điểm của các loại Retinol đang mắc phải trên thị trường, như làm khô da, dễ gây kích ứng, không ổn định. Chiết xuất từ hoa Swertia giúp xua tan những vết khảm thời gian và hồi sinh làn da trẻ trung ngày nào. Tinh thể Hyaluronic Acid siêu nhỏ giúp hấp thụ độ ẩm vào sâu bên trong tế bào da, giúp da luôn căng mọng, ẩm mượt.\n\nRetinol Youth Renewal Serum Murad Việt Nam 1\n\nNgoài ra, Retinol Youth Renewal Serum không hề chứa Paraben, Sulfate, Phthalate, Gluten nên vô cùng lành tính và an toàn cho mọi loại da. Sau 2 tuần sử dụng, bạn sẽ cảm thấy các nếp nhăn biến mất đến 93%, và làn da trở nên mịn màng hơn đến 98%**\n\n* Nguồn: Theo khảo sát thị trường trong 12 tháng và kết thúc vào năm 2017 của The NPD Group, Inc. / U.S. Prestige Beauty Total Measured Market, Retinol Named Skincare Product Sales.\n\n** Theo nghiên cứu lâm sàng của Murad, và kết quả sẽ thay đổi tùy người.\n\nThành phần nổi bật:\n\nNhững thành phần nổi bật có trong Retinol Youth Renewal Serum:\n\n\nCông thức Retinol Tri - Active: Giúp phát huy nhanh chóng công dụng chống lão hóa, làm đều màu da và tạo ra độ rạng rỡ tuyệt đối cho da, nhưng lại vô cùng dịu nhẹ trên da. Cả 3 gốc retinol sẽ thực hiện nhiệm vụ riêng biệt, tuy nhiên, từng công dụng là hỗ trợ nhịp nhàng - đồng điệu với nhau, tạo ra kết quả ưu việt. Cụ thể là:\nRetinol Youth Renewal Serum Murad Việt Nam 2\n\n\n\nRetinol Youth Renewal Serum Murad Việt Nam 3\n\n\n\nChiết xuất từ hoa Swertia: Đây là loài hoa thuộc họ Long đởm, giúp cải thiện độ đàn hồi, từ đó nâng cấp độ săn của da, tạo ra vẻ ngoài trẻ trung, rạng ngời.\nTinh thể Hyaluronic Acid siêu nhỏ: Đưa độ ẩm vào sâu bên trong tế bào da, giúp da luôn căng mọng, mềm mượt, tràn đầy sức sống.\nHướng dẫn sử dụng:\n\nVào mỗi tối, sau khi làm sạch da với sữa rửa mặt, cân bằng độ pH với toner và sử dụng serum/essence, bạn thoa lượng vừa đủ Retinol Youth Renewal Serum. Bạn nên để các sản phẩm ở các bước dưỡng da trước thẩm thấu hoàn toàn, để lại bề mặt da khô thoáng thì sản phẩm sẽ hoạt động tốt hơn. Bạn có thể tránh thoa sản phẩm lên những khu vực da quá nhạy cảm, để tránh những kích ứng không đáng có.\n\nRetinol Youth Renewal Serum Murad Việt Nam 4\n\nBạn hoàn toàn có thể sử dụng Retinol Youth Renewal Eye Cream cho riêng vùng mắt, và kết hợp thêm Retinol Youth Renewal Night Cream vào quy trình dưỡng da buổi tối. Điều đó sẽ giúp bạn đạt được hiệu quả sử dụng tối ưu, và vẻ đẹp hoàn hảo sẽ xuất hiện trên nét da rạng ngời.\n\nKEM CHỐNG NẮNG VÔ HÌNH 3 TRONG 1\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++\n\nCông dụng:\n\nBảo vệ toàn diện. Nuôi dưỡng đủ đầy. Mặt da nhung mịn. Perfecting Shield Broad Spectrum SPF 30 PA +++ ra đời như thể để xóa nhòa ranh giới giữa sản phẩm chăm sóc da và mỹ phẩm trang điểm, giúp làn da nhận được những gì tuyệt hảo nhất của thiên nhiên và công nghệ. Với chiết xuất những loại peptide từ nấm, giúp các dấu hiệu lão hóa dường như tan biến, để lại nét da trẻ trung và căng đầy.\n\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ Murad Việt Nam 1\n\nKhi dùng độc lập, công thức Soft Focus Complex sẽ làm mờ lỗ chân lông, nếp nhăn và các khuyết điểm khác trên da. Khi sử dụng như kem lót, công thức này sẽ giúp kem nền đạt được hiệu ứng hoàn hảo nhất, và giữ cho lớp trang điểm bền bỉ suốt 12 tiếng*. Invisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ còn không hề chứa Parabens, Sulfates, Phthalates, Gluten và các thành phần có chiết xuất từ động vật.\n\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ Murad Việt Nam 2\n\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ còn là kem chống nắng quang phổ rộng cùng co giúp bạn chống được tất cả ảnh hưởng từ tia UVA và UVB. Hơn cả thế, với dạng thức trong suốt tựa như vô hình trên da, giúp bạn tránh được các vệt trắng mà kem chống nắng thông thường hay mắc phải.\n\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ Murad Việt Nam 3\n\nThành phần nổi bật:\n\nNhững thành phần nổi bật có trong Invisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++:\n\nPeptide từ nấm ( Mushroom peptide): Xóa tan các dấu hiệu lão hóa, giúp khôi phục độ săn chắc, đàn hồi cho vẻ ngoài đầy trẻ trung, căng đầy.\nSoft-Focus Complex: Làm mịn lập tức và che mờ các khuyết điểm như lỗ chân lông to, da không đều màu, cấu trúc da sần sùi…\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ Murad Việt Nam 4\n\nChiết xuất Lúa mạch, Hoa hướng dương và Dưa chuột: Nuôi dưỡng hàng rào bảo vệ da, từ đó sức đề kháng của da sẽ được nâng cao, giúp bạn tránh được các tác hại từ môi trường. Đồng thời, khi hàng rào bảo vệ da tự nhiên vững chắc sẽ tạo ra lớp màng ngăn cản quá trình thất thoát độ ẩm và lưu giữ chúng bên trong da, mang đến kết quả làn da căng đầy, mịn màng, ẩm mướt, không còn nếp nhăn.\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ Murad Việt Nam 5\n\nAvobenzone (2.0%), Homosalate (4.0%), Octisalate (4.0%), Octocrylene (2.0%): Giúp chống lại được tia UVA và UVB.\nCông nghệ chống nắng MuraSol độc quyền: Các loại kem chống nắng trên thị trường chỉ có thể tránh được 97-98% tác hại của tia cực tím (hay còn gọi là tia UV/ tia tử ngoại). 2-3% các tia tử ngoại vô tình trót lọt khỏi lớp bảo vệ, sẽ đi vào cơ thể và sản sinh ra các gốc tự do để phá huỷ các tế bào khoẻ mạnh. Bác sĩ Howard Murad đã tạo ra những tiểu phân tử hình cầu có kích thước nano siêu nhỏ (gọi là các hạt phân tử Liposomes), có lõi chứa đầy các chất chống oxy hoá mạnh mẽ nhất. Các phân tử Liposomes siêu nhỏ sẽ luồn lách qua hàng rào bảo vệ và đi sâu vào bên trong da, sau đó phóng thích các chất chống oxy hoá cực mạnh. Có thể nói, công nghệ MuraSol sẽ giúp bảo vệ bạn hoàn hảo trước tác hại của ánh mặt trời.\nHướng dẫn sử dụng:\n\nKhi dùng Invisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ như kem chống nắng, bạn thoa một lượng vừa đủ lên toàn mặt sau khi đã làm sạch da với sữa rửa mặt, dưỡng da với tinh chất/ huyết thanh. Lúc này, sản phẩm không những giúp bảo vệ da trước ánh nắng mặt trời, mà còn tạo lớp màng ngăn cách da tiếp xúc trực tiếp với mỹ phẩm trang điểm, giúp hạn chế tối đa những kích ứng không đáng có.\n\nInvisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ Murad Việt Nam 6\n\nĐể lớp nền hoàn hảo đến mức tuyệt đối, bạn có thể thoa lớp Invisiblur Perfecting Shield Broad Spectrum SPF 30 PA +++ dày hơn những khu vực dễ đổ dầu như: mũi, trán, cằm… để lớp trang điểm được giữ tốt hơn trên da.\n\nMẶT NẠ KÍCH ẨM 400%, HỒI SINH LÀN DA TƯƠI TRẺ THANH XUÂN\n\nHydro-Glow Aqua Peel\n\nCông dụng:\n\nLà bộ sản phẩm 2 bước, với chiếc que Retexturizing Swab được tẩm dung dịch tẩy tế bào chết, cùng chiếc mặt nạ Moisture Infusion Mask mát lạnh thấm đẫm dưỡng chất… Hydro-Glow Aqua Peel sẽ mang đến tận 4 công dụng khác nhau, giúp bạn nhận được quy trình chăm sóc da chuyên nghiệp chuẩn spa tại nhà.\n\nHydro-Glow Aqua Peel Murad Việt Nam 1\n\nChiếc que Retexturizing Swab với thiết kế nhỏ gọn giúp dễ dàng di chuyển vào những ngõ ngách trên gương mặt và lấy đi lớp da chết tạo ra bề mặt láng mịn, Moisture Infusion Mask đem độ ẩm đến từng tế bào da mang theo cảm giác thư giãn tột cùng… Hydro-Glow Aqua Peel sẽ mang đến 4 công dụng: Nhanh - Gọn - Tiện - Hiệu quả tức thì, giúp bạn ngay lập tức có được vẻ đẹp tươi mướt ngập tràn, căng đầy như quả non mọng nước.\n\nHydro-Glow Aqua Peel Murad Việt Nam 2\n\nChỉ với vài phút ngắn ngủi nhưng Hydro-Glow Aqua Peel mang đủ hiệu quả như trải nghiệm tại spa thượng đẳng, làn da sẽ đủ đầy độ ẩm để trở nên tươi tắn, đủ căng mướt để các bước trang điểm thăng hoa, đủ dưỡng chất cho chuyến bay dài khô lạnh, hay là đơn giản như một nghi thức yêu chiều bản thân trong những nhịp sống gấp gáp.\n\nHydro-Glow Aqua Peel Murad Việt Nam 4\n\nThành phần nổi bật:\n\nThành phần nổi bật có trong chiếc que tái tạo Retexturizing Swab của Hydro-Glow Aqua Peel:\n\nGlycolic acid và Lactic acid: Hai thành phần thuộc nhóm AHA (Alpha - Hydroxy Acid) tẩy tế bào chết hóa học - mà bác sĩ Howard Murad đã ứng dụng thường xuyên trong suốt 30 năm trong việc chăm sóc da cho các bệnh nhân/ khách hàng. Hai loại acid này có khả năng nới lỏng các liên kết giữ tế bào chết và bề mặt da. Với đặc tính dịu nhẹ - ổn định và nồng độ hợp lý cho việc dùng tại nhà, Glycolic acid và Lactic acid có trong Hydro-Glow Aqua Peel sẽ phù hợp với hầu hết với mọi loại da. Ngoài ra, hai loại AHA này còn kích thích sự quá trình tạo ra những tế bào mới, đầy khỏe mạnh và tươi trẻ.\nHydro-Glow Aqua Peel Murad Việt Nam 5\nThành phần nổi bật có trong chiếc mặt nạ cấp ẩm Moisture Infusion Mask của Hydro-Glow Aqua Peel:\nHyaluronic Acid: Được biết đến với khả năng giữ được lượng nước gấp 1000 lần so với trọng lượng, thành phần này cung cấp tối đa độ ẩm cho da giúp làn da luôn ẩm mượt, xua tan sự khô ráp, tạo ra nét da căng mướt, tươi sáng tuyệt đối. Đồng thời, Hyaluronic Acid còn dễ dàng thẩm thấu vào sâu bên trong da để làm đầy những nếp nhăn, giúp ngăn ngừa và xua tan những dấu hiệu lão hóa.\nHydro-Glow Aqua Peel Murad Việt Nam 6\n\nChiết xuất Nha đam và Vitamin B3: Cung cấp chất dinh dưỡng, làm dịu những kích ứng và khôi phục - nâng cấp hàng rào bảo vệ da. Khi lớp màng bảo vệ tự nhiên vững chắc, làn da sẽ tránh được những tác động từ môi trường và ngăn sự thất thoát độ ẩm.\nHướng dẫn sử dụng:\n\nSau khi đã làm sạch da hoàn hảo, bạn lấy chiếc tăm bông Retexturizing Swab thoa lên toàn mặt. Bạn không cần rửa lại với nước và tiếp tục đắp mặt nạ Moisture Infusion Mask ngay sau đó. Sau 15 đến 20 phút, bạn lấy miếng mặt nạ ra khỏi da và massage nhẹ nhàng để những dưỡng chất còn vương lại trên da thẩm thấu hoàn toàn vào trong. Tất nhiên, lớp dưỡng chất sẽ hấp thụ hoàn toàn, không tạo cảm giác bết dính khó chịu, nên bạn không cần phải rửa mặt.\n\nHydro-Glow Aqua Peel Murad Việt Nam 7\n\nSau đó, bạn tiếp tục với các sản phẩm dưỡng da khác của Murad (như Essence/Serum, kem dưỡng ẩm) để đạt được hiệu quả chăm sóc da hoàn hảo. Mỗi tuần bạn có thể sử dụng từ 2 - 3 lần Hydro-Glow Aqua Peel đ', NULL, 'Murad', 1, 2, 0, 'bo kit duong am lam mo vet nhan murad (invisiblur perfecting shield broad spectrum spf 30 pa +++ 10ml +aha/bha exfoliating cleanser 30ml +hydro-glow aqua peel + retinol youth renewal serum 5ml)'),
(25, 'Serum trắng da Lariena 24K gold Youthful Serum', '1741041387444', 100, 'Quy cách:30ml/chai\nThành phần:Aqua,Butylen Glycol,Propylene Glycol ,Gold,trehalose,Allantoin,Acrylates/C10-30 Alkyl Acrylate Crosspolymer,Phenoxyethanol,Ethylhexylglycerin,Sodium Hyaluronate(Hyaluronic Acid)\nThương Hiệu Lariena quá nổi tiếng tại Việt NAM trong nhìu năm qua với các dòng quen thuộc serum trắng da whitening 3*10ml/hộp và serum tế bào gốc nhau thai cừu placenta 3*8ml/hộp\n\nThừa kế hiệu quả từ chất lượng đó Cty Lalisse đã kết hợp 4 in 1 cho 2 sản phẩm trên cùng với vàng 24K và HA để sản phẩm trở nên hoàn hảo hơn\n\nSerum vàng 24k Lariena sẽ mang lại cho làn da những lợi ích sau:\n\nLàn da sẽ căng sáng và da sẽ giữ ẩm nhìu hơn.\nGiảm nám,tàn nhang và đồi mồi sau 1 tháng sử dụng\nLỗ chân lông xe khít và dấu hiệu chảy xệ giảm rõ rệt\nGiảm mụn và nhất là sẹo rỗ vì tinh chất nhau thai cừu sẽ làm lành thẹo dù sẹo rỗ to đến đâu cũng được cải thiện\nSản xuất trên dây chuyền hiện đại\nHướng dẫn sử dụng:\nSau khi rửa mặt xong lấy 1 lượng vừa đủ thoa đều hai bên mặt cho đến khi tinh chất thấm sâu vào bên trong da\ncó thể sử dụng trước khi ngủ và sau khi thức dậy vào buổi sáng ngày 2 lần\nSử dụng xong bỏ ngăn mát tủ lạnh để bảo quản\n', 'Úc', 'Lariena', 1, 2, 0, 'serum trang da lariena 24k gold youthful serum'),
(26, 'Kem Dưỡng Ban Ngày Giúp Ngăn Ngừa Lão Hóa Eucerin Hyaluaron- Filler Elasticity (50ml)', '6946307516773', 100, 'Kem Dưỡng Ban Ngày Giúp Ngăn Ngừa Lão Hóa Eucerin Hyaluaron- Filler Elasticity (50ml) dành riêng cho làn da lão hóa có nếp nhăn sâu và kém đàn hồi.\nDòng sản phẩm sở hữu công thức cải tiến chứa cả Hyaluronic Acid phân tử cao và thấp, dễ dàng dung nạp vào da, giúp làm đầy các nếp nhăn rõ rệt.\nĐặc biệt, nhờ sự kết hợp giữa Arctiin – hoạt chất chiết xuất tự nhiên từ cây ngưu bàng, đã được chứng minh công dụng kích thích tái tạo collagen trong tế bào da và giúp da cải thiện độ đàn hồi.\nCùng chất chống oxy hóa Silymarin – chiết xuất tự nhiên từ cây kế sữa, có khả năng bảo vệ collagen và elastin khỏi quá trình lão hóa.\nTừ đó, da trở nên săn chắc, mịn màng và rạng rỡ hơn.\nKem dưỡng ban ngày và ban đêm Eucerin Hyaluron Filler + Elasticity còn chứa các chất béo (lipid) dưỡng ẩm và cải thiện độ đàn hồi cho da.', 'Ba Lan', 'Eucerin', 1, 2, 0, 'kem duong ban ngay giup ngan ngua lao hoa eucerin hyaluaron- filler elasticity (50ml)'),
(27, 'Kem Hồng sâm dưỡng trắng da, chống lão hóa MediQueens công thức mới - Mỹ phẩm Hàn Quốc nhập khẩu chính hãng (50g)', '2105868936114', 100, 'Chống lão hóa, giúp da săn chắc, mềm mịn, làm mờ vết nhăn, ngăn ngừa hình thành vết nhăn mới, se khít lỗ chân lông. Cung cấp các chất dinh dưỡng và độ ẩm cho da, loại bỏ tàn nhang, các đốm nâu, trẻ hóa làn da, ngăn ngừa mụn.\n-HDSD: Rửa mặt thật sạch, lau khô bằng khăn mềm. (Sau bước Toner), cho lượng kem thích hợp lên mặt, vùng cổ hoặc vùng da cần dưỡng, thoa đều, massage nhẹ cho kem thẩm thấu vào da\nSử dụng 1-2 lần/ngày để cho kết quả tốt nhất.\nGiá sản phẩm trên Tiki đã bao gồm thuế theo luật', 'Hàn Quốc', 'MediQueens', 1, 2, 0, 'kem hong sam duong trang da, chong lao hoa mediqueens cong thuc moi - my pham han quoc nhap khau chinh hang (50g)'),
(28, 'Bộ Phấn Phủ Nén Đông Y Nâng Tông Tonymoly / The Oriental Gyeol Goun Two Way Pact SPF48 PA+++', '1358283326928', 100, 'ONYMOLY CAM KẾT HÀNG CHÍNH HÃNG - AN TOÀN CHO DA\n(Sản phẩm Hàn Quốc chính hãng được phân phối độc quyền, cam kết hàng đúng như hình ảnh và mô tả)\nTONYMOLY VIỆT NAM CAM KẾT HOÀN TRẢ TIỀN HÀNG 100% NẾU SẢN PHẨM KHÔNG ĐÚNG NHƯ HÌNH ẢNH VÀ MÔ TẢ - THOẢI MÁI ĐỔI TRẢ HÀNG VÀ HOÀN TRẢ TIỀN HÀNG 100% VỚI BẤT KỲ LÝ DO NÀO KHÔNG ĐÚNG NHƯ CAM KẾT TRONG VÒNG 30 NGÀY.\nI. VỀ THƯƠNG HIỆU TONYMOLY VIỆT NAM:\nTonymoly là thương hiệu về mỹ phẩm và chăm sóc da 100% sản xuất tại Hàn Quốc theo quy trình sản xuất và tiêu chuẩn chất lượng chuẩn quốc tế. Sản phẩm của Tonymoly chủ yếu bao gồm các loại dưỡng da, trang điểm, làm sạch, chăm sóc toàn thân và chăm sóc đặc biệt, với các ưu điểm hoàn toàn nổi trội so với hàng Trung Quốc được sản xuất đại trà hiện nay:\n- Quy trình sản xuất và kiếm soát chất lượng sản phẩm nghiêm ngặt theo tiêu chuẩn quốc tế, vì vậy sản phẩm làm ra có chất lượng vượt trội hơn hẳn so với hàng Trung Quốc.\n- Sử dụng các thành phần tự nhiên nên rất an toàn cho da và thân thiện với môi trường.\n- Dễ dàng sử dụng, hiệu quả lâu dài, không gây tác dụng phụ.\nHãy thử và tự mình so sánh, chắc chắn bạn sẽ cảm nhận được sự khác biệt rõ nét trên mỗi sản phẩm chất lượng cao từ thương hiệu Tonymoly!\nHÃY DÙNG HÀNG CHÍNH HÃNG!\nII. THÔNG TIN VỀ SẢN PHẨM:\n- The Oriental Gyeol Goun Two Way Pact là phấn nén cao cấp chiết xuất từ các thành phần Đông y, tác dụng như một lớp dưỡng da, đồng thời lấy được sắc tố hồng hảo và sức sống cho làn da.\n- Hạt phấn mỏng và mịn có khả năng bám dính tốt, giúp cho làn da mịn màng và mượt mà.\n- Đặc biệt, bộ sản phẩm tặng kèm là tuýp kem nền Gyeol BB Cream, hoàn thiện cho lớp nền hoàn hảo.\nƯU ĐÃI KHI MUA NGAY HÔM NAY:\n- Sản phẩm đang trong chương trình khuyến mãi tri ân khách hàng giá cực sốc.\n- THOẢI MÁI ĐỔI TRẢ HÀNG VÀ HOÀN TRẢ TIỀN HÀNG 100% VỚI BẤT KỲ LÝ DO NÀO KHÔNG ĐÚNG NHƯ CAM KẾT TRONG VÒNG 30 NGÀY.\nHÃY MUA NGAY HÔM NAY ĐỂ NHẬN ĐƯỢC TOÀN BỘ ƯU ĐÃI TỪ TONYMOLY BẠN NHÉ!\n', 'Hàn Quốc', 'Tonymoly', 1, 2, 0, 'bo phan phu nen dong y nang tong tonymoly / the oriental gyeol goun two way pact spf48 pa+++');
