CREATE   PROCEDURE `sp_getProductReport` ()   BEGIN
    SELECT
        sp.Ma,
        sp.TenSanPham,
        ctn.GiaNhap,
        gcn.Gia,
        dvt.Ten,
        ctn.SoLuong,
        SUM(IF(hd.KiemDuyet = 'Đã duyệt', ct.SoLuong, 0)) AS 'BANRA',
        sp.SoLuongTon,
        ctn.SoLuong * ctn.GiaNhap AS 'TONGTIENNHAPVAO',
        SUM(IF(hd.KiemDuyet = 'Đã duyệt', ct.SoLuong * gcn.Gia, 0)) AS 'TONGTIENBANRA',
        SUM(IF(hd.KiemDuyet = 'Đã duyệt', ct.SoLuong * gcn.Gia, 0)) - SUM(ct.SoLuong * ctn.GiaNhap) AS 'DOANHTHU'
    FROM
        tbl_sanpham sp
    LEFT JOIN tbl_dvt dvt ON sp.MaDVT = dvt.Ma
    LEFT JOIN tbl_chitiethoadon ct ON sp.Ma = ct.MaSanPham
    LEFT JOIN tbl_hoadon hd ON ct.MaHoaDon = hd.Ma
    LEFT JOIN tbl_hoadonnhap hdn ON hdn.Ma = sp.Ma
    LEFT JOIN tbl_chitiethoadonnhap ctn ON ctn.MaHoaDonNhap = hdn.Ma
    LEFT JOIN tbl_giasanpham gsp ON gsp.MaSanPham = sp.Ma
    LEFT JOIN tbl_giacapnhat gcn ON gcn.Ma = gsp.MaGia
    GROUP BY
        sp.Ma, sp.TenSanPham, ctn.GiaNhap, gcn.Gia, dvt.Ten, sp.SoLuongTon;
END$$

CREATE   PROCEDURE `sp_getProductReportDateRange` (IN `startDate` DATE, IN `endDate` DATE)   BEGIN
    SELECT
        sp.Ma,
        sp.TenSanPham,
        ctn.GiaNhap,
        gcn.Gia,
        dvt.Ten,
        ctn.SoLuong,
        SUM(IF(hd.KiemDuyet = 'Đã duyệt', ct.SoLuong, 0)) AS 'BANRA',
        sp.SoLuongTon,
        (ctn.SoLuong * ctn.GiaNhap) AS 'TONGTIENNHAPVAO',
        SUM(IF(hd.KiemDuyet = 'Đã duyệt', ct.SoLuong * gcn.Gia, 0)) AS 'TONGTIENBANRA',
        SUM(IF(hd.KiemDuyet = 'Đã duyệt', ct.SoLuong * gcn.Gia, 0)) - SUM(ctn.SoLuong * ctn.GiaNhap) AS 'DOANHTHU'
    FROM
        tbl_sanpham sp
    LEFT JOIN tbl_dvt dvt ON sp.MaDVT = dvt.Ma
    LEFT JOIN tbl_chitiethoadon ct ON sp.Ma = ct.MaSanPham
    LEFT JOIN tbl_hoadon hd ON ct.MaHoaDon = hd.Ma
    LEFT JOIN tbl_hoadonnhap hdn ON hdn.Ma = ct.MaHoaDonNhap
    LEFT JOIN tbl_chitiethoadonnhap ctn ON ctn.MaHoaDonNhap = hdn.Ma
    LEFT JOIN tbl_giasanpham gsp ON gsp.MaSanPham = sp.Ma
    LEFT JOIN tbl_giacapnhat gcn ON gcn.Ma = gsp.MaGia
    WHERE
        hd.NgayLap BETWEEN startDate AND endDate
    GROUP BY
        sp.Ma, sp.TenSanPham, ctn.GiaNhap, gcn.Gia, dvt.Ten, sp.SoLuongTon;
END$$

